(function(global){
  function escapeHtml(html){return html.replace(/&/g,'&amp;').replace(/</g,'&lt;').replace(/>/g,'&gt;');}
  function renderInline(text){return text.replace(/\*\*(.*?)\*\*/g,'<strong>$1</strong>').replace(/\*(.*?)\*/g,'<em>$1</em>').replace(/`([^`]+)`/g,'<code>$1</code>');}
  function parse(md){
    const lines = md.split(/\r?\n/);
    let out='';
    for(let i=0;i<lines.length;i++){const l=lines[i];
      if(/^#\s+/.test(l)){out+=`<h1>${escapeHtml(l.replace(/^#\s+/,''))}</h1>`;}
      else if(/^##\s+/.test(l)){out+=`<h2>${escapeHtml(l.replace(/^##\s+/,''))}</h2>`;}
      else if(/^###\s+/.test(l)){out+=`<h3>${escapeHtml(l.replace(/^###\s+/,''))}</h3>`;}
      else if(/^```/.test(l)){let code='';i++; while(i<lines.length && !/^```/.test(lines[i])){code+=lines[i++]+'\n';} out+=`<pre><code>${escapeHtml(code)}</code></pre>`;}
      else if(/^>\s+/.test(l)){out+=`<blockquote>${escapeHtml(l.replace(/^>\s+/,''))}</blockquote>`;}
      else if(/^\*\s+/.test(l)){ // simple ul
        out+='<ul>'; while(i<lines.length && /^\*\s+/.test(lines[i])){out+=`<li>${renderInline(escapeHtml(lines[i].replace(/^\*\s+/,'')))}</li>`; i++; } out+='</ul>'; i--; }
      else if(/^\s*$/.test(l)){ out+='<p></p>'; }
      else { out+=`<p>${renderInline(escapeHtml(l))}</p>`; }
    }
    return out;
  }
  global.marked={parse};
})(this);
